-- ----------------------------
-- Table structure for gupiao_title_img
-- ----------------------------
DROP TABLE IF EXISTS `gupiao_title_img`;
CREATE TABLE `gupiao_title_img` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) DEFAULT NULL COMMENT '标题',
  `img_path` varchar(1000) DEFAULT NULL COMMENT '内嵌图',
  `content` text COMMENT '内容',
  `background_img` varchar(1000) DEFAULT NULL COMMENT '背景图',
  `type` tinyint(255) DEFAULT NULL COMMENT '模块分类',
  `add_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '添加时间',
  `order_num` tinyint(4) DEFAULT NULL COMMENT '位置',
  `note` varchar(255) DEFAULT NULL COMMENT '注释',
  `class_name` varchar(255) DEFAULT NULL COMMENT '类名',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8;

