INSERT INTO `gupiao_title_img` VALUES ('5', '第二部分', 'banner/2018-01-16/5a5dac742fe05.png', '<p font-size:18px;background-color:#ffffff;\"=\"\" style=\"margin-top: 0px; margin-bottom: 15px; white-space: normal; line-height: 1.4; box-sizing: border-box; padding: 0px; border: none; outline: none; background-image: initial; background-position: center center; background-size: initial; background-repeat: no-repeat; background-attachment: initial; background-origin: initial; background-clip: initial; -webkit-font-smoothing: antialiased; font-size: 16px; color: rgb(64, 64, 64); font-family: Arial, 微软雅黑;\"><span style=\"color:#CCCCCC;\"> <span style=\"color:#666666;\"><span style=\"color:#666666;\">&nbsp;<p font-size:18px;background-color:#ffffff;\"=\"\" style=\"margin-top: 0px; margin-bottom: 15px; white-space: normal; line-height: 1.4; box-sizing: border-box; padding: 0px; border: none; outline: none; background-image: initial; background-position: center center; background-size: initial; background-repeat: no-repeat; background-attachment: initial; background-origin: initial; background-clip: initial; -webkit-font-smoothing: antialiased; font-size: 16px; color: rgb(64, 64, 64); font-family: Arial, 微软雅黑;\"><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;会议强调，党的十八大以来，中央政治局常委会、中央政治局连续3年听取全国人大常委会、国务院、全国政协、最高人民法院、最高人民检察院党组工作汇报和中央书记处工作报告，并将这项工作写入《关于新形势下党内政治生活的若干准则》和《中共中央政治局关于加强和维护党中央集中统一领导的若干规定》。实践证明，这是坚持党中央权威和集中统一领导的重要制度安排，十分必要、很有意义，必须坚持下去。</span>\r\n	</p>\r\n<p font-size:18px;background-color:#ffffff;\"=\"\" style=\"margin-top: 0px; margin-bottom: 15px; white-space: normal; line-height: 1.4; box-sizing: border-box; padding: 0px; border: none; outline: none; background-image: initial; background-position: center center; background-size: initial; background-repeat: no-repeat; background-attachment: initial; background-origin: initial; background-clip: initial; -webkit-font-smoothing: antialiased; font-size: 16px; color: rgb(64, 64, 64); font-family: Arial, 微软雅黑;\"><span style=\"color:#CCCCCC;\"></span><span style=\"color:#666666;\">&nbsp;&nbsp;&nbsp;&nbsp;会议认为，过去的一年，全国人大常委会、国务院、全国政协、最高人民法院、最高人民检察院党组自觉坚持党中央集中统一领导，认真贯彻党中央决策部署，围绕党和国家工作全局履职尽责，保证了党中央重大决策部署贯彻落实，推动各项工作取得了新进展新成效，为实现党和国家事业新发展作出积极贡献。同时，充分发挥党组的领导作用，严格贯彻民主集中制，自觉履行管党治党主体责任，加强党组自身建设，坚持不懈正风肃纪，在全面从严治党上发挥了表率作用。</span>\r\n</p>\r\n</span></span></span>\r\n	</p>\r\n<p font-size:18px;background-color:#ffffff;\"=\"\" style=\"margin-top: 0px; margin-bottom: 15px; white-space: normal; line-height: 1.4; box-sizing: border-box; padding: 0px; border: none; outline: none; background-image: initial; background-position: center center; background-size: initial; background-repeat: no-repeat; background-attachment: initial; background-origin: initial; background-clip: initial; -webkit-font-smoothing: antialiased; font-size: 16px; font-family: Arial, 微软雅黑;\"><span style=\"color:#666666;\"><br />\r\n</span><span style=\"color:#CCCCCC;\"></span>\r\n</p>\r\n<p font-size:18px;background-color:#ffffff;\"=\"\" style=\"margin-top: 0px; margin-bottom: 15px; white-space: normal; line-height: 1.4; box-sizing: border-box; padding: 0px; border: none; outline: none; background-image: initial; background-position: center center; background-size: initial; background-repeat: no-repeat; background-attachment: initial; background-origin: initial; background-clip: initial; -webkit-font-smoothing: antialiased; font-size: 16px; color: rgb(64, 64, 64); font-family: Arial, 微软雅黑;\"><span style=\"color:#666666;\"></span> \r\n	</p>\r\n<p font-size:18px;background-color:#ffffff;\"=\"\" style=\"margin-top: 0px; margin-bottom: 15px; white-space: normal; line-height: 1.4; box-sizing: border-box; padding: 0px; border: none; outline: none; background-image: initial; background-position: center center; background-size: initial; background-repeat: no-repeat; background-attachment: initial; background-origin: initial; background-clip: initial; -webkit-font-smoothing: antialiased; font-size: 16px; color: rgb(64, 64, 64); font-famil