INSERT INTO `gupiao_title_img` VALUES ('9', '第三部分', 'banner/2018-01-16/5a5db0abd3e0f.png', '<p font-size:18px;background-color:#ffffff;\"=\"\" style=\"margin-top: 0px; margin-bottom: 15px; white-space: normal; line-height: 1.4; box-sizing: border-box; padding: 0px; border: none; outline: none; background-image: initial; background-position: center center; background-size: initial; background-repeat: no-repeat; background-attachment: initial; background-origin: initial; background-clip: initial; -webkit-font-smoothing: antialiased; font-size: 16px; color: rgb(64, 64, 64); font-family: Arial, 微软雅黑;\"><span style=\"color:#000000;\">&nbsp;&nbsp;&nbsp;&nbsp;会议认为，过去的一年，中央书记处认真贯彻党中央决策部署，协助党中央推进全面从严治党部署要求贯彻落实，指导做好群团工作，推动党内法规完善实施，配合办好一系列重要会议和重大纪念活动，各方面工作取得了新成绩。</span> \r\n	</p>\r\n<p font-size:18px;background-color:#ffffff;\"=\"\" style=\"margin-top: 0px; margin-bottom: 15px; white-space: normal; line-height: 1.4; box-sizing: border-box; padding: 0px; border: none; outline: none; background-image: initial; background-position: center center; background-size: initial; background-repeat: no-repeat; background-attachment: initial; background-origin: initial; background-clip: initial; -webkit-font-smoothing: antialiased; font-size: 16px; color: rgb(64, 64, 64); font-family: Arial, 微软雅黑;\"><span style=\"color:#666666;\"></span><span style=\"color:#000000;\">&nbsp;&nbsp;&nbsp;&nbsp;会议强调，今年是贯彻党的十九大精神的开局之年，是改革开放40周年，是决胜全面建成小康社会、实施“十三五”规划承上启下的关键一年。全国人大常委会、国务院、全国政协、最高人民法院、最高人民检察院党组要增强“四个意识”，自觉同以习近平同志为核心的党中央保持高度一致，认真学习贯彻习近平新时代中国特色社会主义思想和党的十九大精神，把党的政治建设摆在首位，坚持正确的政治立场、政治方向、政治原则、政治道路，确保令行禁止、政令畅通。要坚持稳中求进工作总基调，大兴调查研究之风，提高决策和执行的科学性和有效性，以锐意进取、奋发有为的精神状态推动党中央大政方针和决策部署落地生根。要带头贯彻党章和《中共中央政治局关于加强和维护党中央集中统一领导的若干规定》，带头执行中央八项规定及其实施细则，严守政治纪律和政治规矩，抓好党组自身建设，推动全面从严治党向纵深发展。</span> \r\n</p>\r\n<p font-size:18px;background-color:#ffffff;\"=\"\" style=\"margin-top: 0px; margin-bottom: 15px; white-space: normal; line-height: 1.4; box-sizing: border-box; padding: 0px; border: none; outline: none; background-image: initial; background-position: center center; background-size: initial; background-repeat: no-repeat; background-attachment: initial; background-origin: initial; background-clip: initial; -webkit-font-smoothing: antialiased; font-size: 16px; color: rgb(64, 64, 64); font-family: Arial, 微软雅黑;\"><span style=\"color:#666666;\"></span><span style=\"color:#000000;\">&nbsp;&nbsp;&nbsp;&nbsp;会议强调，在新的一年里，中央书记处要增强“四个意识”，自觉在中央政治局、中央政治局常委会领导下开展工作，自觉维护习近平总书记党中央的核心、全党的核心地位，把握学习宣传贯彻习近平新时代中国特色社会主义思想和党的十九大精神这条工作主线，观大势、谋全局、议大事，自觉服务大局，突出工作重点，推动党中央重要任务落实见效。</span> \r\n	</p>', 'banner/2018-01-16/5a5db0abd5967.png', '3', '2018-01-17 11:46:16', '3', 'e管家', 'sec3');
